import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import AdminNavbar from '../NavBar/AdminNavbar.jsx';
import './updateitem.css';

const Updateitem = () => {
  const { itemid } = useParams();

  const [values, setValues] = useState({
    itemid: itemid,
    itemname: '',
    description: '',
    startingPrice: '',
    bidEndTime: '',
    imageData: '',
  });

 
  useEffect(() => {
    getItemDetails(); // Fetch item details when the component mounts
  }, []); // Empty dependency array to ensure useEffect runs only once

  // Function to handle input change in form fields
  const onInputChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  };

  // Function to handle form submission
  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      // Your code to submit updated item details to the backend
    } catch (error) {
      console.error('Error updating item details:', error);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div className="updateform">
        <h2 className="formname">Update Item Details Form </h2>
        <div className="form-container">
          <form onSubmit={onSubmit} className="bid-form">
            <div className="mb-3">
              <label htmlFor="itemid" className="form-label">
                ItemId
              </label>
              <input
                type="number"
                className="form-control"
                name="itemid"
                value={values.itemid}
                onChange={onInputChange}
                readOnly
              />
            </div>
            <div className="mb-3">
              <label htmlFor="itemname" className="form-label">
                ItemName
              </label>
              <input
                type="text"
                className="form-control"
                name="itemname"
                value={values.itemname}
                onChange={onInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">
                Description
              </label>
              <input
                type="text"
                className="form-control"
                name="description"
                value={values.description}
                onChange={onInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="startingPrice" className="form-label">
                StartingPrice
              </label>
              <input
                type="number"
                className="form-control"
                name="startingPrice"
                value={values.startingPrice}
                onChange={onInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="bidEndTime" className="form-label">
                BidEndTime
              </label>
              <input
                type="datetime-local"
                className="form-control"
                name="bidEndTime"
                value={values.bidEndTime}
                onChange={onInputChange}
              />
            </div>
            <div>
              <button type="submit" className="update-btn">
                Update
              </button>
              <button type="reset" className="cancel-btn">
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Updateitem;
