    };

  return (
    <div>
      <AdminNavbar />
      <div className='itemdetails'>
        <h2>Item Details</h2>
        <table>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Item Image</th>
              <th>Description</th>
              <th>Starting Price</th>
              <th>Bid End Time</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {itemdetails.map(item => (
              <tr key={item.itemId}>
                <td>{item.itemname}</td>
                <td><img src={`data:image/jpeg;base64,${item.imageData}`} alt={item.itemname} className="thumbnail" /></td>
                <td>{item.description}</td>
                <td>{item.startingPrice}</td>
                <td>{item.bidEndTime}</td>
                <td>
                  {/* Pass item.itemId to handleUpdate and handleDelete functions */}
                  <Link to={`/itemform/${item.itemid}`}><button className='btnupdate'>Update</button></Link>
                  <button onClick={() => handleDelete(item.itemId)} className='btndelete'>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminDashboard;
