import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import AdminNavbar from '../NavBar/AdminNavbar.jsx';
import './updateitem.css';

const Updateitem = () => {
  const { itemid } = useParams();

  const [values, setValues] = useState({
    itemid: itemid, // Ensure itemid is retrieved correctly
    itemname: '',
    description: '',
    startingPrice: '',
    bidEndTime: '',
    imageData: '',
  });

  // Function to fetch item details based on item ID
  const getItemDetails = async () => {
    try {
      const response = await axios.get(`/viewitems/${values.itemid}`); // Use values.itemid
      const itemDetails = response.data;
      // Set the form fields with the retrieved item details
      setValues({
        ...values,
        itemname: itemDetails.itemname,
        description: itemDetails.description,
        startingPrice: itemDetails.startingPrice,
        bidEndTime: itemDetails.bidEndTime,
        imageData: itemDetails.imageData,
      });
    } catch (error) {
      console.error('Error fetching item details:', error);
    }
  };

  useEffect(() => {
    getItemDetails(); // Fetch item details when the component mounts
  }, []); // Empty dependency array to ensure useEffect runs only once

  // Function to handle input change in form fields
  const onInputChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  };

  // Function to handle form submission
  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      // Your code to submit updated item details to the backend
    } catch (error) {
      console.error('Error updating item details:', error);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div className="updateform">
        <h2 className="formname">Update Item Details Form </h2>
        <div className="form-container">
          <form onSubmit={onSubmit} className="bid-form">
            <div className="mb-3">
              <label htmlFor="itemid" className="form-label">
                ItemId
              </label>
              <input
                type="number"
                className="form-control"
                name="itemid"
                value={values.itemid}
                onChange={onInputChange}
                readOnly
              />
            </div>
            {/* Other input fields */}
            {/* Your other form inputs */}
            <div>
              <button type="submit" className="update-btn">
                Update
              </button>
              <button type="reset" className="cancel-btn">
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Updateitem;
